mapper.select:
  	SELECT p.*,i.num,date_format(p.position_creattime, '%Y-%m-%d %H:%i:%s') as time FROM position as p LEFT JOIN 
	(SELECT position_id,count(position_id) as num FROM post_box as i GROUP BY i.position_id) as i ON 
	p.id=i.position_id


       p.user_id = #{userId}


       and p.position_name like concat('%',#{positionName},'%')


       and p.position_status = #{positionStatus,jdbcType=CHAR}

SELECT f.*,p.position_name as positionName,p.id as positionId, date_format(b.post_time, '%Y-%m-%d %H:%i:%s') as postTime ,b.id as postId, i.status as interviewStatus,i.id as interviewId, date_format(i.delay_time, '%Y-%m-%d %H:%i:%s') as delayTime, date_format(i.interview_time, '%Y-%m-%d %H:%i:%s')as interviewTime FROM position as p JOIN post_box as b on p.id=b.position_id JOIN resume_fundation as f ON f.resume_id=b.resume_id left join interview as i ON i.resume_id= f.resume_id and i.position_id=p.id
and p.user_id=#{userId} and p.position_status=#{positionStatus} and b.status=#{status}
and user_name like concat('%',#{userName},'%')
